import React, { useState, useEffect } from 'react';
import GroupItem from '../components/GroupItem'; // Adjust the path as necessary
import './UserGroups.css';
import { useParams, useHistory } from 'react-router-dom';
import Card from '../../shared/components/UIElements/Card';
import Button from '../../shared/components/FormElements/Button';
import Modal from '../../shared/components/UIElements/Modal';
import { getUserGroups } from '../../shared/util/dataGetters'; // Ensure correct import

const UserGroups = () => {
  const { userId } = useParams(); // Destructure userId
  const [userGroups, setUserGroups] = useState([]); // Initialize state for groups
  const [isJoinOpen, setJoinModal] = useState(false);
  const [groupIdInput, setGroupIdInput] = useState('');

  const history = useHistory(); 

  // Fetch user groups asynchronously
  useEffect(() => {
    const fetchGroups = async () => {
      try {
        const groups = await getUserGroups(userId);
        setUserGroups(groups || []); // Fallback to empty array if groups is null/undefined
        console.log("successful fetch");
        console.log(userGroups);
        console.log(groups);
      } catch (error) {
        console.error("Error fetching user groups:", error);
        setUserGroups([]); // Ensure it doesn't break the UI
      }
    };

    fetchGroups();
  }, [userId]); // Run the effect whenever userId changes

  const enterGroupHandler = (id) => {
    console.log(`Entering the group: ` + id);
    console.log(`/group/${id}`);
    history.push(`/group/${id}`);
  };

  const leaveGroupHandler = (groupName) => {
    console.log(`Leaving the group: ${groupName}`);
  };

  const showJoinModalHandler = () => setJoinModal(true);

  const closeJoinModalHandler = () => {
    setJoinModal(false);
    setGroupIdInput(''); // Clear input when modal is closed
  };

  const confirmJoinHandler = () => {
    console.log(`Attempting to join the group with ID: ${groupIdInput}`);
    // Add API or request handling logic here
    closeJoinModalHandler();
  };

  console.log(userGroups);

  return (
    <div>
      <h1>User Groups</h1>
      <div style={{ display: 'flex', justifyContent: 'flex-end', paddingRight: '2rem' }}>
        <button
          className="join-group-button"
          onClick={showJoinModalHandler}
          aria-label="Join a new group"
        >
          Join a Group
        </button>
      </div>

      {/* Modal for joining a group */}
      <Modal
        show={isJoinOpen}
        onCancel={closeJoinModalHandler}
        header="Join Group"
        footer={
          <React.Fragment>
            <Button onClick={closeJoinModalHandler}>Cancel</Button>
            <Button onClick={confirmJoinHandler} type="primary">
              Join
            </Button>
          </React.Fragment>
        }
      >
        <div className="modal-content">
          <label htmlFor="groupIdInput">Enter the group ID</label>
          <input
            id="groupIdInput"
            type="text"
            placeholder="Group ID"
            value={groupIdInput}
            onChange={(e) => setGroupIdInput(e.target.value)}
          />
        </div>
      </Modal>

      <div className="group-list">
        {userGroups.length > 0 ? (
          userGroups.map((group) => (
            <GroupItem
              key={group.id}
              group={group}
              onEnterGroup={() => enterGroupHandler(group.GroupName)}
              onLeaveGroup={() => leaveGroupHandler(group.GroupName)}
            />
          ))
        ) : (
          <p>No groups found for this user.</p>
        )}
      </div>
    </div>
  );
};

export default UserGroups;
