import React, { useState, useEffect } from 'react';
import Button from '../../shared/components/FormElements/Button';
import { useParams } from 'react-router-dom';
import getPost from '../../shared/util/dataGetters';
import { getComments } from '../../shared/util/dataGetters';
import GroupPostComment from './GroupPostComment';
import './GroupPostFull.css';

const GroupPostFull = () => {
    const params  = useParams(); 
    const postId = params.id; 
    console.log("loading post " + postId);
    const [post, setPost] = useState(null);
    const [PostComments, setPostComments] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchPostData = async () => {
            const [fetchedPost, fetchedComments] = await Promise.all([
                getPost(postId),
                getComments(postId)
              ]);
            console.log(fetchedPost);
            console.log(fetchedComments);
            setPost(fetchedPost);
            setPostComments(fetchedComments);
            setLoading(false);
        };
        console.log(post);
        console.log(PostComments); 

        fetchPostData();
    }, [postId]);

    const IncrementMetric = () => {
        console.log("imagine it increments");
    };


    if (loading) {
        return <div>Loading...</div>;
    }

    if (!post) {
        return <div>Post not found</div>;
    }

    return (
        <div>
            <div className="PostBody"> 
                <h1>{post.Title}</h1>
                <div>{post.Text}</div>
                <div className="Metrics">
                    +{post.Rating}
                    <Button onClick={IncrementMetric}>Like</Button>
                </div>    
            </div>
            
            <div className="comment-list"> 
                {PostComments.map((comment) => (
                    <GroupPostComment 
                        key={comment.CommentID}
                        comment={comment}
                    />
                ))}
            </div>
        </div>        
    );
};

export default GroupPostFull;
