import { useEffect, useState } from "react";
import GroupPostTile from "../components/GroupPostTile";
import { useHistory, useParams } from "react-router-dom";  // Import useHistory from react-router-dom v5
import { getGroup, getGroupPosts } from '../../shared/util/dataGetters';
import React from "react";

const Group = () => {
    const params = useParams(); 
    const groupID = params.id; 
   
    //const groupID = 1;
    const history = useHistory();  // Use useHistory instead of useNavigate
    console.log(groupID);
    const expandCard = (id) => {
        console.log("Expanding " + id);
        history.push(`/groupPost/${id}`);  // Use history.push() to navigate
    }

    // Get group data
    const [groupData, setGroupData] = useState(null);
    const [groupPosts, setGroupPosts] = useState(null);
    
    useEffect(() => {
        const fetchGroupData = async () => {
            const fetchedGroup = await getGroup(groupID);
            setGroupData(fetchedGroup);

            const fetchedPosts = await getGroupPosts(groupID);
            setGroupPosts(fetchedPosts);
            console.log(groupPosts);
        }
        fetchGroupData();
    }, [groupID]);
     

    
    return (
        <div>
            {groupData === null || groupPosts === null ? (
                <div> Loading...</div>
            ) : (
                <div>
                    <h1>{groupData.GroupName}</h1>
                    <p>{groupData.Description}</p>
                    <div className="post-list">
                        {groupPosts.map((post) => (
                            <GroupPostTile
                                key={post.PostID}
                                post={post}
                                onExpand={() => expandCard(post.PostID)}  // Pass the post ID to expandCard
                            />
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
}

export default Group;
