import React, { useState, useCallback, createContext } from 'react';
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch
} from 'react-router-dom';

import Users from './user/pages/Users';
import NewPlace from './places/pages/NewPlace';
import UserPlaces from './places/pages/UserPlaces';
import UpdatePlace from './places/pages/UpdatePlace';
import Auth from './user/pages/Auth';
import Groups from './groups/pages/UserGroups'; // Import the Groups page component
import MainNavigation from './shared/components/Navigation/MainNavigation';
import AuthContext  from './shared/context/auth-context';
import GroupPostFull  from './groups/components/GroupPostFull';
import Group from './groups/pages/Group';
import TestPage from './shared/util/TestPage';
import LocationMap from './locations/LocationMap';


const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const [Credentials, setCredentials] = useState({email:"", password: "", loggedIn: false, userId: 0});



  const login = useCallback(() => {
    setIsLoggedIn(true);
  }, []);

  const logout = useCallback(() => {
    setIsLoggedIn(false);
  }, []);

  let routes;

  if (Credentials.loggedIn) {
    routes = (
      <Switch>
        <Route path="/" exact>
          <Users />
        </Route>
        <Route path="/:userId/places" exact>
          <UserPlaces />
        </Route>
        <Route path="/places/new" exact>
          <NewPlace />
        </Route>
        <Route path="/places/:placeId">
          <UpdatePlace />
        </Route>
        <Route path="/:userId/groups" exact>
          <Groups /> {/* The route for the Groups page */}
        </Route>

        <Route path="/groupPost/:id" exact>
          <GroupPostFull/>
        </Route>

        {/* <Route path="/group/:id" element = {<Group/>} /> */}
        <Route path="/group/:id" exact>
          <Group />
        </Route>
        <Route path="/test" exact>
          <TestPage/>
        </Route>
        <Route path="/explore" exact>
          <LocationMap/>
        </Route>
        <Redirect to="/" />

      </Switch>
    );
  } else {
    routes = (
      <Switch>
        <Route path="/" exact>
          <Users />
        </Route>
        <Route path="/:userId/places" exact>
          <UserPlaces />
        </Route>
        <Route path="/auth">
          <Auth />
        </Route>
        <Route path="/test" exact>
          <TestPage/>
        </Route>
        <Redirect to="/auth" />
      </Switch>
    );
  }

  return (
    <AuthContext.Provider
      value={{Credentials, setCredentials}}
    >
      <Router>
        <MainNavigation />
        <main>{routes}</main>
      </Router>
    </AuthContext.Provider>
  );
};


export default App;
