import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import Card from '../../shared/components/UIElements/Card';
import Input from '../../shared/components/FormElements/Input';
import Button from '../../shared/components/FormElements/Button';
import {
  VALIDATOR_EMAIL,
  VALIDATOR_MINLENGTH,
  VALIDATOR_REQUIRE
} from '../../shared/util/validators';
import { useForm } from '../../shared/hooks/form-hook';
import { getAllUsers, getUserId, verifyCredentials } from '../../shared/util/dataGetters';
import AuthContext from '../../shared/context/auth-context';
import './Auth.css';

const Auth = () => {
  const { Credentials, setCredentials } = useContext(AuthContext);
  const [isLoginMode, setIsLoginMode] = useState(true);
  const navigate = useNavigate(); // Initialize useNavigate

  const login = async (formState) => {
    console.log("Starting login");
    const email = formState.inputs.name.value;
    const password = formState.inputs.password.value;
    try {
      const validity = await verifyCredentials(email, password);
      if (validity) {
        const thisId = await getUserId(email);
        setCredentials((prev) => ({
          ...prev,
          email: email,
          password: password,
          loggedIn: true,
          userId: thisId
        }));
        navigate('/myreviews'); // Redirect to /myreviews after successful login
      } else {
        console.log("Invalid credentials");
      }
    } catch (error) {
      console.error("Login error", error);
    }
  };

  const [allUsers, setAllUsers] = useState([]); // Default to an empty array
  const [usersLoading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAllUsers = async () => {
      try {
        const fetchedUsers = await getAllUsers();
        setAllUsers(fetchedUsers || []); // Fallback to empty array if null/undefined
      } catch (error) {
        console.error("Failed to fetch users:", error);
        setAllUsers([]); // Ensure `allUsers` isn't null on error
      } finally {
        setLoading(false);
      }
    };

    fetchAllUsers();
  }, []);

  const [formState, inputHandler, setFormData] = useForm(
    {
      name: {
        value: '',
        isValid: false
      },
      password: {
        value: '',
        isValid: false
      }
    },
    false
  );

  const switchModeHandler = () => {
    if (!isLoginMode) {
      setFormData(
        {
          ...formState.inputs,
          name: undefined
        },
        formState.inputs.email.isValid && formState.inputs.password.isValid
      );
    } else {
      setFormData(
        {
          ...formState.inputs,
          name: {
            value: '',
            isValid: false
          }
        },
        false
      );
    }
    setIsLoginMode((prevMode) => !prevMode);
  };

  const authSubmitHandler = (event) => {
    event.preventDefault();
    login(formState);
  };

  return (
    <React.Fragment>
      <Card className="authentication">
        <h2>Login Required</h2>
        <hr />
        <form onSubmit={authSubmitHandler}>
          {!isLoginMode && (
            <Input
              element="input"
              id="name"
              type="text"
              label="Your Full Name"
              validators={[VALIDATOR_REQUIRE()]}
              errorText="Please enter your full name."
              onInput={inputHandler}
            />
          )}
          <Input
            element="input"
            id="name"
            type="text"
            label="Username"
            validators={[VALIDATOR_REQUIRE()]}
            errorText="Please enter a valid username."
            onInput={inputHandler}
          />
          <Input
            element="input"
            id="password"
            type="password"
            label="Password"
            validators={[VALIDATOR_MINLENGTH(5)]}
            errorText="Please enter a valid password, at least 5 characters."
            onInput={inputHandler}
          />
          <Button type="submit" disabled={!formState.isValid}>
            {isLoginMode ? 'LOGIN' : 'SIGNUP'}
          </Button>
        </form>
        <Button inverse onClick={switchModeHandler}>
          SWITCH TO {isLoginMode ? 'SIGNUP' : 'LOGIN'}
        </Button>
      </Card>
      <div className="userDisplay">
        <h1>List of All Valid User Credentials</h1>
        {usersLoading ? (
          <p>Loading users...</p>
        ) : allUsers.length > 0 ? (
          allUsers.map((user, index) => (
            <div style={{ padding: '2px' }} key={index}>
              <p>{user.Name}</p>
              <p>{user.Password}</p>
            </div>
          ))
        ) : (
          <p>No users found.</p>
        )}
      </div>
    </React.Fragment>
  );
};

export default Auth;
