import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getLocation, getLocationReviews } from '../shared/util/dataGetters';  // Assuming these functions are implemented

const LocationPage = () => {
  const { id } = useParams();  // Get the location ID from the URL
  const [location, setLocation] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLocationData = async () => {
      try {
        const locationData = await getLocation(id);  // Use the ID from the URL
        setLocation(locationData);
      } catch (error) {
        console.error('Error fetching location data:', error);
      }
    };

    const fetchLocationReviews = async () => {
      try {
        const reviewsData = await getLocationReviews(id);  // Use the ID from the URL
        setReviews(reviewsData || []);  // Ensure reviews is an array
      } catch (error) {
        console.error('Error fetching location reviews:', error);
      }
    };

    fetchLocationData();
    fetchLocationReviews();
    setLoading(false);
  }, [id]);  // Dependency array includes `id` so it refetches when the URL changes

  if (loading) {
    return <div>Loading location and reviews...</div>;
  }

  return (
    <div className="location-page">
      {location && (
        <div className="location-info">
          <h1 className="location-title">{location.Name}</h1>
          <p className="location-description">{location.Description}</p>
          <div className="location-rating">
            <strong>Overall Rating: </strong>{location.OverallRating || 'Not rated'}
          </div>
          <div className="location-coordinates">
            <strong>Coordinates: </strong>{location.Coordinates}
          </div>
        </div>
      )}

      <div className="location-reviews">
        <h2>Reviews</h2>
        {reviews.data.length === 0 ? (
          <p>No reviews available for this location.</p>
        ) : (
          reviews.map((review, index) => (
            <div key={index} className="review">
              <h3 className="review-title">{review.Title}</h3>
              <p className="review-text">{review.Text}</p>
              {review.Media && <img src={review.Media} alt="Review Media" className="review-media" />}
              <div className="review-rating">
                <strong>Rating: </strong>{review.Rating || 'Not rated'}
              </div>
              <div className="review-date">
                <strong>Date: </strong>{new Date(review.Date).toLocaleDateString()}
              </div>
              <div className="review-member">
                <strong>Reviewed by Member ID: </strong>{review.MemberID}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default LocationPage;
